import{Utils}from"./utils.js";import{PreloaderUtils}from"./preloaderUtils.js";let utils,preloader;document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("#change-to-login-view"),t=document.querySelector("#change-to-register-view");utils=new Utils,(preloader=new PreloaderUtils).init(),e.addEventListener("click",()=>utils.changeView(utils.views.login_view)),t.addEventListener("click",()=>utils.changeView(utils.views.register_view))}),window.addEventListener("load",()=>{setTimeout(()=>{utils.hidePreloader().then(()=>{utils.init()})},1e3)});